    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int le,q,a=0,e=0,i=0,o=0,u=0,t;
        String n;
        char ac,ec,ic,oc,uc;
        n=t1.getText();
        le=n.length();
        for(q=0;q<le;q++)
        {
        ac=n.charAt(q);
        if(ac=='a'){
          a++;}
          
          ec=n.charAt(q);
 
        if(ec=='e'){
          e++;}
          
          ic=n.charAt(q);
        if(ic=='i'){
          i++;}
          
          oc=n.charAt(q);
        if(oc=='o'){
          o++;}
          
          uc=n.charAt(q);
        if(uc=='u'){
          u++;}
        
        }
        t=a+e+i+o+u;
        t1.setText(n);
        
        t3.setText(Integer.toString(a));
        t4.setText(Integer.toString(e));
        t5.setText(Integer.toString(i));
        t6.setText(Integer.toString(o));
        t7.setText(Integer.toString(u));
        t8.setText(Integer.toString(t));
          
            
        
        
        
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

